import { useEffect, useState } from "react";
import {  useSelector } from "react-redux";
import CartItem from "../components/CartItem";
import { Link } from "react-router-dom";

const Cart = () => {
  const[totalamount,settotalamount]=useState(0);
  const cart =useSelector((state)=>state)
  useEffect(()=>{
    settotalamount(cart.reduce((acc,curr)=>acc+curr.price,0))
  },[cart])
  
  return (<div className="max-w-[1200px] mx-auto flex flex-col md:flex-row justify-center">
    {
      cart.length>0?(
        <div>
          <div className="w-[100%] md:w-[">
          {cart.map((item)=>(<CartItem key={item.id} item={item}/>))}
          </div>
            
          
          <div>
           <div >
           <p>Your Cart</p>
           <p>Summary</p>
           <p>Total items: {cart.length}</p>
           </div>
            <div>
            <p>Total Amount: ${totalamount}</p>
            <button>Checkout Now</button></div>
          </div>
        </div>
      ):(<div className="min-h-[80vh] flex flex-col items-center justify-center">
          <h1 className="text-gray-700 font-semibold text-xl mb-2">Your cart is empty!</h1>
          <Link to='/'><button className="bg-green-600 hover:bg-purple-50 rounded-lg text-white transition-300 ease-linear
          mt-5 border-2 border-green-600 font-semibold hover:text-green-700 p-3 px-10 tracking-wider">SHOP NOW</button></Link>
      </div>)
    }
  </div>);
};

export default Cart;
