import { useDispatch } from "react-redux";
import { FcDeleteDatabase } from "react-icons/fc";
import { remove } from "../redux/Slices/cartSlice";
import toast from "react-hot-toast";

const CartItem = ({item}) => {
  const dispatch=useDispatch();
  function removeitem(){
    dispatch(remove(item.id))
    toast.error("Item removed")
  }
  return (
    <div className="flex items-center p-2 md:p-5 justify-between border-b-2 border-slate-500 mt-2 mb-2 md:mx-5">
      <div className="flex flex-col md:flex-row p-0 md:p-3 gap-5 items-center">
      <img src={item.image} className="object-cover"/>
      
      <div className="md:ml-10 self-start ">
        <p>{item.title}</p>
        <p>{item.description.split(" ").slice(0,10).join(" ")+'...'}</p>
        <div>
          <p>${item.price}</p>
          <FcDeleteDatabase onClick={removeitem}/>
        </div>
      </div>
      </div>
      
    </div>
  )
};

export default CartItem;
