import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { add, remove } from '../redux/Slices/cartSlice';
import toast from 'react-hot-toast';

const Product = ({post}) => {
  const cart =useSelector((state)=>state)
  const dispatch =useDispatch();
  function additems(){
    dispatch(add(post));
    toast.success("Items added to cart")
  }
  function removeitems(){
    dispatch(remove(post.id));
    toast.error("Items removed from cart")
  }
  return (
    <div className='flex flex-col items-center justify-between hover:scale-110 transition duration-300 
    ease-in gap-3 p-4 mt-10 ml-3 rounded-xl outline'>
      <div>
        <p className='text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1'>{post.title}</p>
      </div>
      <div>
        <p className='w-40 text-gray-400 font-normal text-[10px] text-left'>{post.description.split(" ").slice(0,10).join(" ")+"..."}</p>
      </div>
      <div className='h-[180px]'><img src={post.image} className='h-full w-full'/></div>
      <div className='flex justify-between gay-12 items-center w-full mt-5'>
        <p className='text-green-600 font-semibold'>${post.price}</p>
        {!cart.some((p)=>p.id==post.id) ?(
          <button onClick={additems} className='text-gray-700 border-2 border-gray-700 rounded-full font-semibold text-[12px] p-1 px-3 uppercase 
          hover:bg-gray-700 hover:text-white transition duration-300 '>Add To Cart</button>
        ):(<button onClick={removeitems}>Remove From Cart</button>)}
      </div>
    </div>
  )
}

export default Product
