import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { add, remove } from '../redux/Slices/cartSlice';
import toast from 'react-hot-toast';

const Product = ({post}) => {
  const cart =useSelector((state)=>state)
  const dispatch =useDispatch();
  function additems(){
    dispatch(add(post));
    toast.success("Items added to cart")
  }
  function removeitems(){
    dispatch(remove(post.id));
    toast.error("Items removed from cart")
  }
  return (
    <div className='flex flex-col items-center justify-between hover'>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description.split(" ").slice(0,10).join(" ")+"..."}</p>
      </div>
      <div><img src={post.image}/></div>
      <div>
        <p>${post.price}</p>
        {!cart.some((p)=>p.id==post.id) ?(
          <button onClick={additems}>Add To Cart</button>
        ):(<button onClick={removeitems}>Remove From Cart</button>)}
      </div>
    </div>
  )
}

export default Product
